import React, {useRef, useState} from 'react'

const IndexUseRef = () => {

    const imageRef = useRef(null);

    const [isFirstImage, setisFirstImage] = useState(true);

    const handleImageChange =()=>
    {
        if(imageRef.current)
        {
            imageRef.current.src = isFirstImage?"data:image/jpeg;base64," : "data:image/jpeg;base64,"

            setisFirstImage(!isFirstImage);
        }
    };

  return (
    <div>
      <h1>Change image using useRef hook</h1>
      <img ref={imageRef} src="data:image/jpeg;base64," alt="" />

      <button onClick={handleImageChange}>Change Image</button>
    </div>
  )
}

export default IndexUseRef;
